:0:"";s:11:"vod_content";s:804:"艾瑞（Bank Prasongsup 饰）和同性男友交往多年，彼此之间感情十分稳定，深深相爱的两人希望能够共同养育爱情的结晶，他们想了一个注意，找来了一位女孩做他们的代孕妈妈很快，一个可爱完美的宝宝降生了，取名为阿努（Freshy Tuangphop 饰）。艾瑞和男友沉浸在喜悦之中，然而好景不长，在一场意外中，男友不幸遇难，艾瑞在悲痛欲绝中独自承担起抚养孩子的重任。&nbsp;　　随着时间的推移，阿努一天天长大，成为了一个英俊帅气的小伙子，然而，因为艾瑞特殊的身份，阿努常常遭到旁人的讥笑，这令父子两人之间的关系格外紧张。与此同时，艾瑞混乱的人际关系亦将整个家庭推向了濒临破碎的边缘。";s:13:"vod_play_from";s:8:"subom3u8";s:15:"vod_play_server";s:2:"no";s:13:"vod_play_note";s:0:"";s:12:"vod_play_url";s:60:"HD高清$https://www.183757.com/20190719/prcub7H0/index.m3u8";s:13:"vod_down_from";s:0:"";s:15:"vod_down_server";s:0:"";s:13:"vod_down_note";s:0:"";s:12:"vod_down_url";s:0:"";s:13:"vod_play_list";a:1:{i:1;a:9:{s:3:"sid";i:1;s:11:"player_info";a:9:{s:6:"status";s:1:"1";s:4:"from";s:8:"subom3u8";s:4:"show";s:15:"【速播线】";s:3:"des";s:12:"自动连播";s:2:"ps";s:1:"1";s:5:"parse";s:18:"//z1.m1907.cn/?jx=";s:4:"sort";s:3:"400";s:3:"tip";s:27:"右下角短时出现水印";s:2:"id";s:8:"subom3u8";}s:11:"server_info";N;s:4:"from";s:8:"subom3u8";s:3:"url";s:60:"HD高清$https://www.183757.com/20190719/prcub7H0/index.m3u8";s:6:"server";s:2:"no";s:4:"note";N;s:9:"url_count";i:1;s:4:"urls";a:1:{i:1;a:4:{s:4:"name";s:8:"HD高清";s:3:"url";s:51:"https://www.183757.com/20190719/prcub7H0/index.m3u8";s:4:"from";s:8:"subom3u8";s:3:"nid";i:1;}}}}s:4:"type";a:18:{s:7:"type_id";i:20;s:9:"type_name";s:6:"福利";s:7:"type_en";s:4:"fuli";s:9:"type_sort";i:5;s:8:"type_mid";i:1;s:8:"type_pid";i:0;s:11:"type_status";i:1;s:8:"type_tpl";s:9:"type.html";s:13:"type_tpl_list";s:9:"show.html";s:15:"type_tpl_detail";s:11:"detail.html";s:13:"type_tpl_play";s:9:"play.html";s:13:"type_tpl_down";s:9:"down.html";s:8:"type_key";s:0:"";s:8:"type_des";s:0:"";s:10:"type_title";s:0:"";s:10:"type_union";s:0:"";s:11:"type_extend";a:8:{s:5:"class";s:0:"";s:4:"area";s:0:"";s:4:"lang";s:0:"";s:4:"year";s:0:"";s:4:"star";s:0:"";s:8:"director";s:0:"";s:5:"state";s:0:"";s:7:"version";s:0:"";}s:8:"childids";N;}s:6:"type_1";N;}